import api from './apiConfig';

const SystemService = {
  pcMove: async (board, sessionId) => {
    try {
      const response = await api.post('/game/pc_move', {
        board,
        sessionId,
      });

      const data = response.data;
      return {
        ...data,
        board: typeof data.board === 'string' ? JSON.parse(data.board) : data.board,
        status: data.status || 'ongoing',
      };
    } catch (error) {
      console.error('PC move API error:', error);
      if (error.response) {
        console.error('Error response:', error.response.data);
      }
      throw error.response?.data || { error: 'Failed to make PC move' };
    }
  }
};

export default SystemService;
